require("./index");

var m = M();

var testCases = [
    [0,0],
    [{},1],
    [{},2],
    ["foo",3],
    ["bar",4],
    [null,5],
    [false,6],
    [true,7],
    [function x(){},8]
]

testCases.forEach(function(t) {
    m(t[0], t[1]);
    assert(m(t[0]) === t[1]);
})
testCases.forEach(function(t) {
    assert(m(t[0]) === t[1]);
})

testCases.forEach(function(t) {
    m(t[1], t[0]);
    assert(m(t[1]) === t[0]);
})
testCases.forEach(function(t) {
    assert(m(t[1]) === t[0]);
})

testCases.forEach(function(t) {
    m(t[0], t[0]);
    assert(m(t[0]) === t[0]);
})
testCases.forEach(function(t) {
    assert(m(t[0]) === t[0]);
})

function assert(condition) {
    if (!condition) throw new Error("Assertion Error");
}
